package pattern;

public class pattern8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=8;
		for(int i=1;i<a;i++) {
		int	b=i<(a/2)?a-i:i;
		
			for(int j=b;j>0;j--) {
				System.out.print("  ");
			}
			for(int k=a-b;k>0;k--) {
				System.out.print("  * ");
			
			
			
		}System .out.println();

	}

}}
