package array;

public class PrintingArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] array;

		array = new int[5];
		array[0] = 10;
		array[1] = 20;
		array[2] = 30;
		array[3] = 40;
		array[4] = 50;
		for(int i=0;i<5;i++) {
			System.out.println(array[i]);
		}

	}

}
