package string;

public class String17 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="hello paul hrhhe kkk kkk jgfys jhgfasj jhg";
		int count=0;
		for(int i=0;i<a.length();i++) {
			char b=a.charAt(i);
			if(b==' ') {
				count++;
			}
			
		}
		count++;
		System.out.println("the lenth  "+count);
		
		

	}

}
